"use strict";(self.webpackChunkmindly_client=self.webpackChunkmindly_client||[]).push([[330],{6330:(n,t,h)=>{h.d(t,{quotes:()=>i});const i=[{text:"H\u1ea1nh ph\xfac kh\xf4ng ph\u1ea3i l\xe0 \u0111\xedch \u0111\u1ebfn, m\xe0 l\xe0 c\xe1ch ta \u0111i",author:"Th\xedch Nh\u1ea5t H\u1ea1nh"},{text:"B\xecnh an \u0111\u1ebfn t\u1eeb b\xean trong, \u0111\u1eebng t\xecm ki\u1ebfm n\xf3 t\u1eeb b\xean ngo\xe0i",author:"Buddha"},{text:"Y\xeau th\u01b0\u01a1ng b\u1ea3n th\xe2n l\xe0 kh\u1edfi \u0111\u1ea7u c\u1ee7a m\u1ed9t cu\u1ed9c phi\xeau l\u01b0u su\u1ed1t \u0111\u1eddi",author:"Oscar Wilde"},{text:"\u0110\u1eebng \u0111\u1ec3 ng\xe0y h\xf4m qua chi\u1ebfm qu\xe1 nhi\u1ec1u th\u1eddi gian c\u1ee7a ng\xe0y h\xf4m nay",author:"Will Rogers"},{text:"Cu\u1ed9c s\u1ed1ng l\xe0 10% \u0111i\u1ec1u x\u1ea3y ra v\u1edbi b\u1ea1n v\xe0 90% l\xe0 c\xe1ch b\u1ea1n ph\u1ea3n \u1ee9ng v\u1edbi n\xf3",author:"Charles R. Swindoll"},{text:"Khi b\u1ea1n thay \u0111\u1ed5i c\xe1ch nh\xecn v\u1ec1 m\u1ecdi th\u1ee9, nh\u1eefng th\u1ee9 b\u1ea1n nh\xecn c\u0169ng s\u1ebd thay \u0111\u1ed5i",author:"Wayne Dyer"},{text:"M\u1ed7i h\u01a1i th\u1edf l\xe0 m\u1ed9t m\xf3n qu\xe0 c\u1ee7a cu\u1ed9c s\u1ed1ng",author:"Th\xedch Nh\u1ea5t H\u1ea1nh"},{text:"C\xe1ch t\u1ed1t nh\u1ea5t \u0111\u1ec3 d\u1ef1 \u0111o\xe1n t\u01b0\u01a1ng lai l\xe0 t\u1ea1o ra n\xf3.",author:"Peter Drucker"},{text:"B\u1ea1n kh\xf4ng bao gi\u1edd qu\xe1 gi\xe0 \u0111\u1ec3 \u0111\u1eb7t ra m\u1ed9t m\u1ee5c ti\xeau kh\xe1c ho\u1eb7c m\u01a1 m\u1ed9t gi\u1ea5c m\u01a1 m\u1edbi.",author:"C.S. Lewis"},{text:"H\xe0nh tr\xecnh ng\xe0n d\u1eb7m b\u1eaft \u0111\u1ea7u t\u1eeb m\u1ed9t b\u01b0\u1edbc ch\xe2n.",author:"L\xe3o T\u1eed"},{text:"Nh\u1eefng g\xec b\u1ea1n nh\u1eadn \u0111\u01b0\u1ee3c khi \u0111\u1ea1t \u0111\u01b0\u1ee3c m\u1ee5c ti\xeau kh\xf4ng quan tr\u1ecdng b\u1eb1ng con ng\u01b0\u1eddi b\u1ea1n tr\u1edf th\xe0nh khi \u0111\u1ea1t \u0111\u01b0\u1ee3c m\u1ee5c ti\xeau.",author:"Zig Ziglar"},{text:"Gi\u1edbi h\u1ea1n duy nh\u1ea5t cho vi\u1ec7c hi\u1ec7n th\u1ef1c h\xf3a ng\xe0y mai c\u1ee7a ch\xfang ta s\u1ebd l\xe0 nh\u1eefng nghi ng\u1edd c\u1ee7a ch\xfang ta v\u1ec1 ng\xe0y h\xf4m nay.",author:"Franklin D. Roosevelt"},{text:"Kh\xf4ng quan tr\u1ecdng b\u1ea1n \u0111i ch\u1eadm nh\u01b0 th\u1ebf n\xe0o mi\u1ec5n l\xe0 b\u1ea1n kh\xf4ng d\u1eebng l\u1ea1i.",author:"Kh\u1ed5ng T\u1eed"},{text:"M\u1ecdi th\u1ee9 b\u1ea1n t\u1eebng mong mu\u1ed1n \u0111\u1ec1u \u1edf ph\xeda b\xean kia c\u1ee7a s\u1ef1 s\u1ee3 h\xe3i.",author:"George Addair"},{text:"H\xe3y tin v\xe0o b\u1ea3n th\xe2n v\xe0 t\u1ea5t c\u1ea3 nh\u1eefng g\xec b\u1ea1n \u0111ang c\xf3. Bi\u1ebft r\u1eb1ng c\xf3 m\u1ed9t c\xe1i g\xec \u0111\xf3 b\xean trong b\u1ea1n v\u0129 \u0111\u1ea1i h\u01a1n b\u1ea5t k\u1ef3 tr\u1edf ng\u1ea1i n\xe0o.",author:"Christian D. Larson"},{text:"B\xed quy\u1ebft \u0111\u1ec3 ti\u1ebfn l\xean l\xe0 b\u1eaft \u0111\u1ea7u.",author:"Mark Twain"},{text:"\xdd ch\xed \u0111\u1ec3 chi\u1ebfn th\u1eafng, khao kh\xe1t \u0111\u1ec3 th\xe0nh c\xf4ng, s\u1ef1 th\xf4i th\xfac \u0111\u1ec3 \u0111\u1ea1t \u0111\u01b0\u1ee3c ti\u1ec1m n\u0103ng t\u1ed1i \u0111a c\u1ee7a b\u1ea1n... \u0111\xe2y l\xe0 nh\u1eefng chi\u1ebfc ch\xeca kh\xf3a s\u1ebd m\u1edf ra c\xe1nh c\u1eeda \u0111\u1ebfn v\u1edbi s\u1ef1 xu\u1ea5t s\u1eafc c\u1ee7a c\xe1 nh\xe2n.",author:"Kh\u1ed5ng T\u1eed"},{text:"Th\u1eddi gian c\u1ee7a b\u1ea1n c\xf3 h\u1ea1n, \u0111\u1eebng l\xe3ng ph\xed n\xf3 \u0111\u1ec3 s\u1ed1ng cu\u1ed9c \u0111\u1eddi c\u1ee7a ng\u01b0\u1eddi kh\xe1c.",author:"Steve Jobs"},{text:"Hai ng\xe0y quan tr\u1ecdng nh\u1ea5t trong cu\u1ed9c \u0111\u1eddi b\u1ea1n l\xe0 ng\xe0y b\u1ea1n \u0111\u01b0\u1ee3c sinh ra v\xe0 ng\xe0y b\u1ea1n t\xecm ra l\xfd do t\u1ea1i sao.",author:"Mark Twain"},{text:"Ng\u01b0\u1eddi duy nh\u1ea5t b\u1ea1n \u0111\u01b0\u1ee3c \u0111\u1ecbnh s\u1eb5n \u0111\u1ec3 tr\u1edf th\xe0nh l\xe0 ng\u01b0\u1eddi m\xe0 b\u1ea1n quy\u1ebft \u0111\u1ecbnh tr\u1edf th\xe0nh.",author:"Ralph Waldo Emerson"},{text:"H\xe3y t\u1ef1 tin \u0111i theo h\u01b0\u1edbng \u01b0\u1edbc m\u01a1 c\u1ee7a b\u1ea1n. S\u1ed1ng cu\u1ed9c s\u1ed1ng m\xe0 b\u1ea1n \u0111\xe3 t\u01b0\u1edfng t\u01b0\u1ee3ng.",author:"Henry David Thoreau"},{text:"Khi t\xf4i bu\xf4ng b\u1ecf con ng\u01b0\u1eddi c\u1ee7a m\xecnh, t\xf4i tr\u1edf th\xe0nh con ng\u01b0\u1eddi m\xe0 t\xf4i c\xf3 th\u1ec3 tr\u1edf th\xe0nh.",author:"L\xe3o T\u1eed"},{text:"B\u1ea1n l\xe0 ch\u1ee7 nh\xe2n c\u1ee7a s\u1ed1 ph\u1eadn c\u1ee7a m\xecnh. B\u1ea1n c\xf3 th\u1ec3 \u1ea3nh h\u01b0\u1edfng, ch\u1ec9 \u0111\u1ea1o v\xe0 ki\u1ec3m so\xe1t m\xf4i tr\u01b0\u1eddng c\u1ee7a ri\xeang m\xecnh. B\u1ea1n c\xf3 th\u1ec3 bi\u1ebfn cu\u1ed9c s\u1ed1ng c\u1ee7a m\xecnh th\xe0nh nh\u1eefng g\xec b\u1ea1n mu\u1ed1n.",author:"Napoleon Hill"},{text:"H\xe3y x\xe2y d\u1ef1ng \u01b0\u1edbc m\u01a1 c\u1ee7a ri\xeang b\u1ea1n, ho\u1eb7c ng\u01b0\u1eddi kh\xe1c s\u1ebd thu\xea b\u1ea1n \u0111\u1ec3 x\xe2y d\u1ef1ng \u01b0\u1edbc m\u01a1 c\u1ee7a h\u1ecd.",author:"Farrah Gray"},{text:"T\u01b0\u01a1ng lai ph\u1ee5 thu\u1ed9c v\xe0o nh\u1eefng g\xec b\u1ea1n l\xe0m h\xf4m nay.",author:"Mahatma Gandhi"},{text:"T\xe2m tr\xed l\xe0 t\u1ea5t c\u1ea3. B\u1ea1n ngh\u0129 g\xec b\u1ea1n s\u1ebd tr\u1edf th\xe0nh nh\u01b0 v\u1eady.",author:"Ph\u1eadt"},{text:"Ho\u1eb7c b\u1ea1n \u0111i\u1ec1u h\xe0nh m\u1ed9t ng\xe0y, ho\u1eb7c m\u1ed9t ng\xe0y \u0111i\u1ec1u h\xe0nh b\u1ea1n.",author:"Jim Rohn"},{text:"T\xf4i kh\xf4ng th\u1ea5t b\u1ea1i. T\xf4i ch\u1ec9 t\xecm ra 10.000 c\xe1ch kh\xf4ng ho\u1ea1t \u0111\u1ed9ng.",author:"Thomas A. Edison"},{text:"N\u1ebfu b\u1ea1n kh\xf4ng s\u1eb5n s\xe0ng m\u1ea1o hi\u1ec3m nh\u1eefng \u0111i\u1ec1u th\xf4ng th\u01b0\u1eddng, b\u1ea1n s\u1ebd ph\u1ea3i ch\u1ea5p nh\u1eadn nh\u1eefng \u0111i\u1ec1u b\xecnh th\u01b0\u1eddng.",author:"Jim Rohn"},{text:"T\u1ea5t c\u1ea3 nh\u1eefng gi\u1ea5c m\u01a1 c\u1ee7a ch\xfang ta \u0111\u1ec1u c\xf3 th\u1ec3 tr\u1edf th\xe0nh s\u1ef1 th\u1eadt, n\u1ebfu ch\xfang ta c\xf3 can \u0111\u1ea3m \u0111\u1ec3 theo \u0111u\u1ed5i ch\xfang.",author:"Walt Disney"},{text:"N\u1ebfu b\u1ea1n l\xe0m nh\u1eefng g\xec b\u1ea1n \u0111\xe3 lu\xf4n l\xe0m, b\u1ea1n s\u1ebd nh\u1eadn \u0111\u01b0\u1ee3c nh\u1eefng g\xec b\u1ea1n \u0111\xe3 lu\xf4n nh\u1eadn \u0111\u01b0\u1ee3c.",author:"Tony Robbins"},{text:"S\u1ef1 tr\u1ea3 th\xf9 t\u1ed1t nh\u1ea5t l\xe0 th\xe0nh c\xf4ng vang d\u1ed9i.",author:"Frank Sinatra"},{text:"T\xf4i kh\xf4ng ph\u1ea3i l\xe0 s\u1ea3n ph\u1ea9m c\u1ee7a ho\xe0n c\u1ea3nh c\u1ee7a t\xf4i. T\xf4i l\xe0 s\u1ea3n ph\u1ea9m c\u1ee7a nh\u1eefng quy\u1ebft \u0111\u1ecbnh c\u1ee7a t\xf4i.",author:"Stephen Covey"},{text:"Kh\xf4ng c\xf3 gi\u1edbi h\u1ea1n n\xe0o cho nh\u1eefng g\xec b\u1ea1n c\xf3 th\u1ec3 ho\xe0n th\xe0nh, ngo\u1ea1i tr\u1eeb nh\u1eefng gi\u1edbi h\u1ea1n b\u1ea1n \u0111\u1eb7t ra cho suy ngh\u0129 c\u1ee7a ch\xednh m\xecnh.",author:"Brian Tracy"},{text:"M\u1ed9t ng\u01b0\u1eddi \u0111\xe0n \xf4ng th\xe0nh c\xf4ng l\xe0 ng\u01b0\u1eddi c\xf3 th\u1ec3 \u0111\u1eb7t m\u1ed9t n\u1ec1n m\xf3ng v\u1eefng ch\u1eafc b\u1eb1ng nh\u1eefng vi\xean g\u1ea1ch m\xe0 ng\u01b0\u1eddi kh\xe1c \u0111\xe3 n\xe9m v\xe0o anh ta.",author:"David Brinkley"},{text:"Kh\xf4ng ai c\xf3 th\u1ec3 khi\u1ebfn b\u1ea1n c\u1ea3m th\u1ea5y th\u1ea5p k\xe9m n\u1ebfu kh\xf4ng c\xf3 s\u1ef1 \u0111\u1ed3ng \xfd c\u1ee7a b\u1ea1n.",author:"Eleanor Roosevelt"},{text:"C\xe2u h\u1ecfi kh\xf4ng ph\u1ea3i l\xe0 ai s\u1ebd cho ph\xe9p t\xf4i; m\xe0 l\xe0 ai s\u1ebd ng\u0103n c\u1ea3n t\xf4i.",author:"Ayn Rand"},{text:"N\u1ebfu b\u1ea1n mu\u1ed1n n\xe2ng m\xecnh l\xean, h\xe3y n\xe2ng ng\u01b0\u1eddi kh\xe1c l\xean.",author:"Booker T. Washington"},{text:"Nh\u1eefng g\xec \u0111\u1ed1i v\u1edbi ch\xfang ta l\xe0 nh\u1eefng th\u1eed th\xe1ch cay \u0111\u1eafng th\u01b0\u1eddng l\xe0 nh\u1eefng ph\u01b0\u1edbc l\xe0nh tr\xe1 h\xecnh.",author:"Oscar Wilde"},{text:"Kho\u1ea3ng c\xe1ch gi\u1eefa s\u1ef1 \u0111i\xean r\u1ed3 v\xe0 thi\xean t\xe0i ch\u1ec9 \u0111\u01b0\u1ee3c \u0111o b\u1eb1ng th\xe0nh c\xf4ng.",author:"Bruce Feirstein"},{text:"Khi b\u1ea1n ng\u1eebng theo \u0111u\u1ed5i nh\u1eefng \u0111i\u1ec1u sai tr\xe1i, b\u1ea1n s\u1ebd cho nh\u1eefng \u0111i\u1ec1u \u0111\xfang \u0111\u1eafn m\u1ed9t c\u01a1 h\u1ed9i \u0111\u1ec3 b\u1eaft k\u1ecbp b\u1ea1n.",author:"Lolly Daskal"},{text:"T\xf4i kh\xf4ng th\u1ec3 thay \u0111\u1ed5i h\u01b0\u1edbng gi\xf3, nh\u01b0ng t\xf4i c\xf3 th\u1ec3 \u0111i\u1ec1u ch\u1ec9nh c\xe1nh bu\u1ed3m c\u1ee7a m\xecnh \u0111\u1ec3 lu\xf4n \u0111\u1ebfn \u0111\u01b0\u1ee3c \u0111\xedch.",author:"Jimmy Dean"},{text:"Di chuy\u1ec3n nhanh v\xe0 ph\xe1 v\u1ee1 m\u1ecdi th\u1ee9. Tr\u1eeb khi b\u1ea1n \u0111ang ph\xe1 v\u1ee1 m\u1ecdi th\u1ee9, b\u1ea1n kh\xf4ng di chuy\u1ec3n \u0111\u1ee7 nhanh.",author:"Mark Zuckerberg"},{text:"Kh\xf4ng ph\u1ea3i g\xe1nh n\u1eb7ng l\xe0m b\u1ea1n g\u1ee5c ng\xe3, m\xe0 l\xe0 c\xe1ch b\u1ea1n mang n\xf3.",author:"Lou Holtz"},{text:"T\xf4i c\xe0ng l\xe0m vi\u1ec7c ch\u0103m ch\u1ec9, t\xf4i c\xe0ng may m\u1eafn.",author:"Gary Player"},{text:"\u0110i\u1ec1u duy nh\u1ea5t \u0111\u1ee9ng gi\u1eefa b\u1ea1n v\xe0 m\u1ee5c ti\xeau c\u1ee7a b\u1ea1n l\xe0 c\xe2u chuy\u1ec7n nh\u1ea3m nh\xed m\xe0 b\u1ea1n kh\xf4ng ng\u1eebng k\u1ec3 cho b\u1ea3n th\xe2n nghe v\u1ec1 l\xfd do t\u1ea1i sao b\u1ea1n kh\xf4ng th\u1ec3 \u0111\u1ea1t \u0111\u01b0\u1ee3c n\xf3.",author:"Jordan Belfort"},{text:"N\u1ebfu b\u1ea1n \u0111ang l\xe0m vi\u1ec7c g\xec \u0111\xf3 m\xe0 b\u1ea1n th\u1ef1c s\u1ef1 quan t\xe2m, b\u1ea1n kh\xf4ng c\u1ea7n ph\u1ea3i b\u1ecb th\xfac \xe9p. T\u1ea7m nh\xecn s\u1ebd k\xe9o b\u1ea1n \u0111i.",author:"Steve Jobs"},{text:"\u0110i\u1ec1u v\u1ec1 nh\u1eefng kh\u1edfi \u0111\u1ea7u m\u1edbi l\xe0 ch\xfang \u0111\xf2i h\u1ecfi m\u1ed9t c\xe1i g\xec \u0111\xf3 kh\xe1c ph\u1ea3i k\u1ebft th\xfac.",author:"Unknown"},{text:"V\xe0 \u0111\u1ed9t nhi\xean b\u1ea1n bi\u1ebft: \u0110\xe3 \u0111\u1ebfn l\xfac b\u1eaft \u0111\u1ea7u m\u1ed9t c\xe1i g\xec \u0111\xf3 m\u1edbi v\xe0 tin t\u01b0\u1edfng v\xe0o s\u1ef1 k\u1ef3 di\u1ec7u c\u1ee7a nh\u1eefng kh\u1edfi \u0111\u1ea7u.",author:"Meister Eckhart"},{text:"B\u01b0\u1edbc \u0111\u1ea7u ti\xean \u0111\u1ec3 \u0111\u1ebfn m\u1ed9t n\u01a1i n\xe0o \u0111\xf3 l\xe0 quy\u1ebft \u0111\u1ecbnh r\u1eb1ng b\u1ea1n s\u1ebd kh\xf4ng \u1edf l\u1ea1i n\u01a1i b\u1ea1n \u0111ang \u1edf.",author:"J.P. Morgan"},{text:"C\xf3 nh\u1eefng \u0111i\u1ec1u t\u1ed1t \u0111\u1eb9p h\u01a1n nhi\u1ec1u \u1edf ph\xeda tr\u01b0\u1edbc h\u01a1n b\u1ea5t k\u1ef3 \u0111i\u1ec1u g\xec ch\xfang ta \u0111\u1ec3 l\u1ea1i ph\xeda sau.",author:"C.S. Lewis"},{text:"B\xed quy\u1ebft \u0111\u1ec3 thay \u0111\u1ed5i l\xe0 t\u1eadp trung t\u1ea5t c\u1ea3 n\u0103ng l\u01b0\u1ee3ng c\u1ee7a b\u1ea1n, kh\xf4ng ph\u1ea3i v\xe0o vi\u1ec7c ch\u1ed1ng l\u1ea1i c\xe1i c\u0169, m\xe0 l\xe0 v\xe0o vi\u1ec7c x\xe2y d\u1ef1ng c\xe1i m\u1edbi.",author:"Socrates"},{text:"Ho\xe0n c\u1ea3nh hi\u1ec7n t\u1ea1i c\u1ee7a b\u1ea1n kh\xf4ng quy\u1ebft \u0111\u1ecbnh n\u01a1i b\u1ea1n c\xf3 th\u1ec3 \u0111\u1ebfn; ch\xfang ch\u1ec9 \u0111\u01a1n thu\u1ea7n x\xe1c \u0111\u1ecbnh n\u01a1i b\u1ea1n b\u1eaft \u0111\u1ea7u.",author:"Nido Qubein"},{text:"M\u1ed7i ng\xe0y l\xe0 m\u1ed9t c\u01a1 h\u1ed9i \u0111\u1ec3 b\u1eaft \u0111\u1ea7u l\u1ea1i. \u0110\u1eebng t\u1eadp trung v\xe0o nh\u1eefng th\u1ea5t b\u1ea1i c\u1ee7a ng\xe0y h\xf4m qua, h\xe3y b\u1eaft \u0111\u1ea7u ng\xe0y h\xf4m nay v\u1edbi nh\u1eefng suy ngh\u0129 v\xe0 k\u1ef3 v\u1ecdng t\xedch c\u1ef1c.",author:"Catherine Pulsifer"},{text:"B\u1ea1n c\xf3 th\u1ec3 h\u1ecdc nh\u1eefng \u0111i\u1ec1u m\u1edbi b\u1ea5t c\u1ee9 l\xfac n\xe0o trong \u0111\u1eddi n\u1ebfu b\u1ea1n s\u1eb5n s\xe0ng tr\u1edf th\xe0nh ng\u01b0\u1eddi m\u1edbi b\u1eaft \u0111\u1ea7u. N\u1ebfu b\u1ea1n th\u1ef1c s\u1ef1 h\u1ecdc c\xe1ch th\xedch l\xe0m ng\u01b0\u1eddi m\u1edbi b\u1eaft \u0111\u1ea7u, c\u1ea3 th\u1ebf gi\u1edbi s\u1ebd m\u1edf ra v\u1edbi b\u1ea1n.",author:"Barbara Sher"},{text:"M\u1ed9t b\xecnh minh m\u1edbi, m\u1ed9t ng\xe0y m\u1edbi, m\u1ed9t cu\u1ed9c s\u1ed1ng m\u1edbi... v\xe0 t\xf4i c\u1ea3m th\u1ea5y t\u1ed1t.",author:"Nina Simone"},{text:"Ph\xe9p m\xe0u trong nh\u1eefng kh\u1edfi \u0111\u1ea7u m\u1edbi th\u1ef1c s\u1ef1 l\xe0 m\u1ea1nh m\u1ebd nh\u1ea5t trong t\u1ea5t c\u1ea3.",author:"Josiyah Martin"},{text:"\u0110\xe2y l\xe0 m\u1ed9t n\u0103m m\u1edbi. M\u1ed9t kh\u1edfi \u0111\u1ea7u m\u1edbi. V\xe0 m\u1ecdi th\u1ee9 s\u1ebd thay \u0111\u1ed5i.",author:"Taylor Swift"},{text:"T\u01b0\u01a1ng lai b\u1eaft \u0111\u1ea7u t\u1eeb h\xf4m nay.",author:"Wayne Gerard Trotman"},{text:"H\xe3y t\u1ea1o ra t\u01b0\u01a1ng lai c\u1ee7a ch\xfang ta ngay b\xe2y gi\u1edd, v\xe0 h\xe3y bi\u1ebfn \u01b0\u1edbc m\u01a1 c\u1ee7a ch\xfang ta th\xe0nh hi\u1ec7n th\u1ef1c c\u1ee7a ng\xe0y mai.",author:"Malala Yousafzai"},{text:"\u0110i\u1ec1u t\u1ed1t nh\u1ea5t v\u1eabn ch\u01b0a \u0111\u1ebfn.",author:"Frank Sinatra"},{text:"\u0110\xf3 l\xe0 m\u1ed9t b\xecnh minh m\u1edbi, \u0111\xf3 l\xe0 m\u1ed9t ng\xe0y m\u1edbi, \u0111\xf3 l\xe0 m\u1ed9t cu\u1ed9c s\u1ed1ng m\u1edbi cho t\xf4i v\xe0 t\xf4i c\u1ea3m th\u1ea5y t\u1ed1t.",author:"Leslie Bricusse and Anthony Newley"},{text:"S\u1ef1 kh\u1edfi \u0111\u1ea7u l\xe0 ph\u1ea7n quan tr\u1ecdng nh\u1ea5t c\u1ee7a c\xf4ng vi\u1ec7c.",author:"Plato"},{text:"C\xe1i m\xe0 ch\xfang ta g\u1ecdi l\xe0 s\u1ef1 kh\u1edfi \u0111\u1ea7u th\u01b0\u1eddng l\xe0 s\u1ef1 k\u1ebft th\xfac. V\xe0 \u0111\u1ec3 k\u1ebft th\xfac l\xe0 \u0111\u1ec3 b\u1eaft \u0111\u1ea7u. S\u1ef1 k\u1ebft th\xfac l\xe0 n\u01a1i ch\xfang ta b\u1eaft \u0111\u1ea7u.",author:"T.S. Eliot"},{text:"B\u1ea1n kh\xf4ng th\u1ec3 quay l\u1ea1i v\xe0 thay \u0111\u1ed5i s\u1ef1 kh\u1edfi \u0111\u1ea7u, nh\u01b0ng b\u1ea1n c\xf3 th\u1ec3 b\u1eaft \u0111\u1ea7u t\u1eeb n\u01a1i b\u1ea1n \u0111ang \u1edf v\xe0 thay \u0111\u1ed5i k\u1ebft th\xfac.",author:"C.S. Lewis"},{text:"H\xe3y bu\xf4ng b\u1ecf ng\xe0y h\xf4m qua. H\xe3y \u0111\u1ec3 h\xf4m nay l\xe0 m\u1ed9t kh\u1edfi \u0111\u1ea7u m\u1edbi v\xe0 h\xe3y l\xe0 ng\u01b0\u1eddi t\u1ed1t nh\u1ea5t c\xf3 th\u1ec3, v\xe0 b\u1ea1n s\u1ebd \u0111\u1ebfn \u0111\u01b0\u1ee3c n\u01a1i Ch\xfaa mu\u1ed1n b\u1ea1n \u0111\u1ebfn.",author:"Joel Osteen"},{text:"Ch\xfang ta ph\u1ea3i s\u1eb5n s\xe0ng t\u1eeb b\u1ecf cu\u1ed9c s\u1ed1ng m\xe0 ch\xfang ta \u0111\xe3 l\xean k\u1ebf ho\u1ea1ch, \u0111\u1ec3 c\xf3 \u0111\u01b0\u1ee3c cu\u1ed9c s\u1ed1ng \u0111ang ch\u1edd \u0111\u1ee3i ch\xfang ta. L\u1edbp da c\u0169 ph\u1ea3i \u0111\u01b0\u1ee3c l\u1ed9t b\u1ecf tr\u01b0\u1edbc khi l\u1edbp da m\u1edbi c\xf3 th\u1ec3 \u0111\u1ebfn.",author:"Joseph Campbell"},{text:"M\u1ed9t cu\u1ed9c h\xe0nh tr\xecnh ng\xe0n d\u1eb7m ph\u1ea3i b\u1eaft \u0111\u1ea7u b\u1eb1ng m\u1ed9t b\u01b0\u1edbc ch\xe2n.",author:"L\xe3o T\u1eed"},{text:"H\xe3y b\u1eaft \u0111\u1ea7u t\u1eeb n\u01a1i b\u1ea1n \u0111ang \u1edf. S\u1eed d\u1ee5ng nh\u1eefng g\xec b\u1ea1n c\xf3. L\xe0m nh\u1eefng g\xec b\u1ea1n c\xf3 th\u1ec3.",author:"Arthur Ashe"},{text:"C\xe1ch \u0111\u1ec3 b\u1eaft \u0111\u1ea7u l\xe0 ng\u1eebng n\xf3i v\xe0 b\u1eaft \u0111\u1ea7u l\xe0m.",author:"Walt Disney"},{text:"\u0110\u1eebng ng\u1ea1i t\u1eeb b\u1ecf c\xe1i t\u1ed1t \u0111\u1ec3 \u0111i t\xecm c\xe1i v\u0129 \u0111\u1ea1i.",author:"John D. Rockefeller"},{text:"T\xf4i th\u1ea5y r\u1eb1ng t\xf4i c\xe0ng l\xe0m vi\u1ec7c ch\u0103m ch\u1ec9, t\xf4i c\xe0ng c\xf3 v\u1ebb may m\u1eafn.",author:"Thomas Jefferson"},{text:"Th\xe0nh c\xf4ng l\xe0 t\u1ed5ng c\u1ee7a nh\u1eefng n\u1ed7 l\u1ef1c nh\u1ecf, \u0111\u01b0\u1ee3c l\u1eb7p \u0111i l\u1eb7p l\u1ea1i h\xe0ng ng\xe0y.",author:"Robert Collier"},{text:"N\u1ebfu b\u1ea1n mu\u1ed1n \u0111\u1ea1t \u0111\u01b0\u1ee3c s\u1ef1 v\u0129 \u0111\u1ea1i, h\xe3y ng\u1eebng xin ph\xe9p.",author:"Unknown"},{text:"M\u1ecdi vi\u1ec7c di\u1ec5n ra t\u1ed1t nh\u1ea5t cho nh\u1eefng ng\u01b0\u1eddi t\u1eadn d\u1ee5ng t\u1ed1t nh\u1ea5t c\xe1ch m\u1ecdi vi\u1ec7c di\u1ec5n ra.",author:"John Wooden"},{text:"\u0110\u1ec3 s\u1ed1ng m\u1ed9t cu\u1ed9c s\u1ed1ng s\xe1ng t\u1ea1o, ch\xfang ta ph\u1ea3i m\u1ea5t \u0111i n\u1ed7i s\u1ee3 b\u1ecb sai.",author:"Unknown"},{text:"N\u1ebfu b\u1ea1n kh\xf4ng s\u1eb5n s\xe0ng m\u1ea1o hi\u1ec3m nh\u1eefng \u0111i\u1ec1u th\xf4ng th\u01b0\u1eddng, b\u1ea1n s\u1ebd ph\u1ea3i ch\u1ea5p nh\u1eadn nh\u1eefng \u0111i\u1ec1u b\xecnh th\u01b0\u1eddng.",author:"Jim Rohn"},{text:"H\xe3y tin t\u01b0\u1edfng v\xec b\u1ea1n s\u1eb5n s\xe0ng ch\u1ea5p nh\u1eadn r\u1ee7i ro, kh\xf4ng ph\u1ea3i v\xec n\xf3 an to\xe0n hay ch\u1eafc ch\u1eafn.",author:"Unknown"},{text:"H\xe3y ch\u1ecdn m\u1ed9t \xfd t\u01b0\u1edfng. Bi\u1ebfn \xfd t\u01b0\u1edfng \u0111\xf3 th\xe0nh cu\u1ed9c s\u1ed1ng c\u1ee7a b\u1ea1n - ngh\u0129 v\u1ec1 n\xf3, m\u01a1 v\u1ec1 n\xf3, s\u1ed1ng v\u1edbi \xfd t\u01b0\u1edfng \u0111\xf3. H\xe3y \u0111\u1ec3 b\u1ed9 n\xe3o, c\u01a1 b\u1eafp, d\xe2y th\u1ea7n kinh, m\u1ecdi b\u1ed9 ph\u1eadn c\u1ee7a c\u01a1 th\u1ec3 b\u1ea1n, tr\xe0n \u0111\u1ea7y \xfd t\u01b0\u1edfng \u0111\xf3, v\xe0 ch\u1ec9 \u0111\u1ec3 l\u1ea1i m\u1ecdi \xfd t\u01b0\u1edfng kh\xe1c m\u1ed9t m\xecnh. \u0110\xe2y l\xe0 con \u0111\u01b0\u1eddng d\u1eabn \u0111\u1ebfn th\xe0nh c\xf4ng.",author:"Swami Vivekananda"},{text:"T\u1ea5t c\u1ea3 nh\u1eefng gi\u1ea5c m\u01a1 c\u1ee7a ch\xfang ta \u0111\u1ec1u c\xf3 th\u1ec3 tr\u1edf th\xe0nh s\u1ef1 th\u1eadt n\u1ebfu ch\xfang ta c\xf3 can \u0111\u1ea3m \u0111\u1ec3 theo \u0111u\u1ed5i ch\xfang.",author:"Walt Disney"},{text:"Th\xe0nh c\xf4ng l\xe0 \u0111i t\u1eeb th\u1ea5t b\u1ea1i n\xe0y \u0111\u1ebfn th\u1ea5t b\u1ea1i kh\xe1c m\xe0 kh\xf4ng m\u1ea5t \u0111i s\u1ef1 nhi\u1ec7t t\xecnh.",author:"Winston Churchill"},{text:"Ngay khi con s\xe2u b\u01b0\u1edbm ngh\u0129 r\u1eb1ng th\u1ebf gi\u1edbi s\u1eafp k\u1ebft th\xfac, n\xf3 \u0111\xe3 bi\u1ebfn th\xe0nh m\u1ed9t con b\u01b0\u1edbm.",author:"T\u1ee5c ng\u1eef"},{text:"B\u1ea5t c\u1ee9 khi n\xe0o b\u1ea1n nh\xecn th\u1ea5y m\u1ed9t ng\u01b0\u1eddi th\xe0nh c\xf4ng, b\u1ea1n ch\u1ec9 nh\xecn th\u1ea5y nh\u1eefng vinh quang c\xf4ng khai, kh\xf4ng bao gi\u1edd l\xe0 nh\u1eefng hy sinh ri\xeang t\u01b0 \u0111\u1ec3 \u0111\u1ea1t \u0111\u01b0\u1ee3c ch\xfang.",author:"Vaibhav Shah"},{text:"C\u01a1 h\u1ed9i kh\xf4ng t\u1ef1 nhi\xean x\u1ea3y ra, b\u1ea1n t\u1ea1o ra ch\xfang.",author:"Chris Grosser"},{text:"\u0110\u1eebng c\u1ed1 g\u1eafng tr\u1edf th\xe0nh m\u1ed9t ng\u01b0\u1eddi th\xe0nh c\xf4ng, m\xe0 h\xe3y c\u1ed1 g\u1eafng tr\u1edf th\xe0nh m\u1ed9t ng\u01b0\u1eddi c\xf3 gi\xe1 tr\u1ecb.",author:"Albert Einstein"},{text:"Nh\u1eefng b\u1ed9 \xf3c v\u0129 \u0111\u1ea1i th\u1ea3o lu\u1eadn v\u1ec1 \xfd t\u01b0\u1edfng; nh\u1eefng b\u1ed9 \xf3c trung b\xecnh th\u1ea3o lu\u1eadn v\u1ec1 s\u1ef1 ki\u1ec7n; nh\u1eefng b\u1ed9 \xf3c nh\u1ecf b\xe9 th\u1ea3o lu\u1eadn v\u1ec1 con ng\u01b0\u1eddi.",author:"Eleanor Roosevelt"},{text:"T\xf4i kh\xf4ng th\u1ea5t b\u1ea1i. T\xf4i ch\u1ec9 t\xecm ra 10.000 c\xe1ch kh\xf4ng ho\u1ea1t \u0111\u1ed9ng.",author:"Thomas A. Edison"},{text:"N\u1ebfu b\u1ea1n kh\xf4ng coi tr\u1ecdng th\u1eddi gian c\u1ee7a m\xecnh, ng\u01b0\u1eddi kh\xe1c c\u0169ng s\u1ebd kh\xf4ng. H\xe3y ng\u1eebng cho \u0111i th\u1eddi gian v\xe0 t\xe0i n\u0103ng c\u1ee7a b\u1ea1n - h\xe3y b\u1eaft \u0111\u1ea7u t\xednh ph\xed cho n\xf3.",author:"Kim Garst"},{text:"M\u1ed9t ng\u01b0\u1eddi \u0111\xe0n \xf4ng th\xe0nh c\xf4ng l\xe0 ng\u01b0\u1eddi c\xf3 th\u1ec3 \u0111\u1eb7t m\u1ed9t n\u1ec1n m\xf3ng v\u1eefng ch\u1eafc b\u1eb1ng nh\u1eefng vi\xean g\u1ea1ch m\xe0 ng\u01b0\u1eddi kh\xe1c \u0111\xe3 n\xe9m v\xe0o anh ta.",author:"David Brinkley"},{text:"Kh\xf4ng ai c\xf3 th\u1ec3 khi\u1ebfn b\u1ea1n c\u1ea3m th\u1ea5y th\u1ea5p k\xe9m n\u1ebfu kh\xf4ng c\xf3 s\u1ef1 \u0111\u1ed3ng \xfd c\u1ee7a b\u1ea1n.",author:"Eleanor Roosevelt"},{text:"To\xe0n b\u1ed9 b\xed quy\u1ebft c\u1ee7a m\u1ed9t cu\u1ed9c s\u1ed1ng th\xe0nh c\xf4ng l\xe0 t\xecm ra v\u1eadn m\u1ec7nh c\u1ee7a m\u1ed9t ng\u01b0\u1eddi \u0111\u1ec3 l\xe0m, v\xe0 sau \u0111\xf3 l\xe0m \u0111i\u1ec1u \u0111\xf3.",author:"Henry Ford"},{text:"N\u1ebfu b\u1ea1n \u0111ang \u0111i qua \u0111\u1ecba ng\u1ee5c, h\xe3y ti\u1ebfp t\u1ee5c \u0111i.",author:"Winston Churchill"},{text:"Nh\u1eefng ng\u01b0\u1eddi \u0111\u1ee7 \u0111i\xean r\u1ed3 \u0111\u1ec3 ngh\u0129 r\u1eb1ng h\u1ecd c\xf3 th\u1ec3 thay \u0111\u1ed5i th\u1ebf gi\u1edbi, ch\xednh l\xe0 nh\u1eefng ng\u01b0\u1eddi l\xe0m \u0111\u01b0\u1ee3c \u0111i\u1ec1u \u0111\xf3.",author:"V\xf4 danh"},{text:"\u0110\u1eebng l\xean gi\u1ecdng, h\xe3y c\u1ea3i thi\u1ec7n l\u1eadp lu\u1eadn c\u1ee7a b\u1ea1n.",author:"V\xf4 danh"},{text:"Nh\u1eefng g\xec \u0111\u1ed1i v\u1edbi ch\xfang ta l\xe0 nh\u1eefng th\u1eed th\xe1ch cay \u0111\u1eafng th\u01b0\u1eddng l\xe0 nh\u1eefng ph\u01b0\u1edbc l\xe0nh tr\xe1 h\xecnh.",author:"Oscar Wilde"},{text:"\xdd ngh\u0129a c\u1ee7a cu\u1ed9c s\u1ed1ng l\xe0 t\xecm ra m\xf3n qu\xe0 c\u1ee7a b\u1ea1n. M\u1ee5c \u0111\xedch c\u1ee7a cu\u1ed9c s\u1ed1ng l\xe0 cho \u0111i n\xf3.",author:"V\xf4 danh"},{text:"Kho\u1ea3ng c\xe1ch gi\u1eefa s\u1ef1 \u0111i\xean r\u1ed3 v\xe0 thi\xean t\xe0i ch\u1ec9 \u0111\u01b0\u1ee3c \u0111o b\u1eb1ng th\xe0nh c\xf4ng.",author:"Bruce Feirstein"},{text:"Khi b\u1ea1n ng\u1eebng theo \u0111u\u1ed5i nh\u1eefng \u0111i\u1ec1u sai tr\xe1i, b\u1ea1n s\u1ebd cho nh\u1eefng \u0111i\u1ec1u \u0111\xfang \u0111\u1eafn m\u1ed9t c\u01a1 h\u1ed9i \u0111\u1ec3 b\u1eaft k\u1ecbp b\u1ea1n.",author:"Lolly Daskal"},{text:"T\xf4i tin r\u1eb1ng l\xf2ng can \u0111\u1ea3m duy nh\u1ea5t m\xe0 b\u1ea5t k\u1ef3 ai c\u0169ng c\u1ea7n l\xe0 l\xf2ng can \u0111\u1ea3m \u0111\u1ec3 theo \u0111u\u1ed5i \u01b0\u1edbc m\u01a1 c\u1ee7a ri\xeang m\xecnh.",author:"Oprah Winfrey"},{text:"Kh\xf4ng c\xf3 ki\u1ec7t t\xe1c n\xe0o \u0111\u01b0\u1ee3c t\u1ea1o ra b\u1edfi m\u1ed9t ngh\u1ec7 s\u0129 l\u01b0\u1eddi bi\u1ebfng.",author:"V\xf4 danh"},{text:"N\u1ebfu b\u1ea1n kh\xf4ng th\u1ec3 gi\u1ea3i th\xedch n\xf3 m\u1ed9t c\xe1ch \u0111\u01a1n gi\u1ea3n, b\u1ea1n kh\xf4ng hi\u1ec3u n\xf3 \u0111\u1ee7 r\xf5.",author:"Albert Einstein"},{text:"'H\xe3y h\u1ecdc c\xe1ch ch\u1ea5p nh\u1eadn v\xe0 tha th\u1ee9 cho nh\u1eefng l\u1ed3i l\u1ea7m c\u1ee7a b\u1ea3n th\xe2n, v\xec con ng\u01b0\u1eddi ai c\u0169ng m\u1eafc sai l\u1ea7m",author:"Unknown"}]}}]);
